import timeit

def digital_root_rec(n):
    s = 0
    while n:
        s += n % 10
        n //= 10

    return s if s < 10 else digital_root_rec(s)


def digital_root(n): return n % 9 or n and 9

print(f"Tempo de execução: {timeit.timeit(lambda: print(digital_root_rec(), end='; '), number=1)} segundos")
print(f"Tempo de execução: {timeit.timeit(lambda: print(digital_root(), end='; '), number=1)} segundos")